package project_lab_test2;

public class Alternate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int[] arr = { 10, 20, 30, 40, 50, 60, 70 };
	    int N = arr.length;
	    {
	    	for(int currIndex = 0;
	            currIndex < N;
	            currIndex++)
	    	     
	            if (currIndex % 2 == 0)
	            
	                System.out.print(arr[currIndex] + " ");	
}
}
}
